/**
 * Check if a point is within the green pixel boundaries of the grass block
 * Uses pre-computed Y ranges for each X coordinate
 */
export const isPointInGrassArea = (
  x: number,
  y: number,
  platformWidth: number,
  platformHeight: number
): boolean => {
  // Image dimensions from blockofdirt.png
  const imageWidth = 2485;
  const imageHeight = 1893;

  // Convert platform coordinates (relative to sheep container) to image pixel coordinates
  const imageX = Math.round((x / platformWidth) * imageWidth);
  const imageY = Math.round((y / platformHeight) * imageHeight);

  // Look up the green pixel range for this X coordinate
  const range = GREEN_PIXEL_RANGES[imageX.toString()];
  if (!range) return false;

  const [minY, maxY] = range;
  return imageY >= minY && imageY <= maxY;
};

/**
 * Generate a random position within the diamond grass block
 * Uses rejection sampling to ensure the point is within the green area
 */
export const getRandomPositionInDiamond = (
  platformWidth: number,
  platformHeight: number,
  sheepSize: number = 60
): { x: number; y: number } => {
  let x, y;
  let attempts = 0;
  const maxAttempts = 100;

  // Rejection sampling: keep trying until we get a point inside the grass area
  do {
    x = Math.random() * platformWidth;
    y = Math.random() * platformHeight;
    attempts++;
  } while (
    !isPointInGrassArea(x, y, platformWidth, platformHeight) &&
    attempts < maxAttempts
  );

  // Clamp to ensure sheep doesn't go out of bounds
  x = Math.max(sheepSize / 2, Math.min(platformWidth - sheepSize / 2, x));
  y = Math.max(sheepSize / 2, Math.min(platformHeight - sheepSize / 2, y));

  return { x, y };
};
